package leetcode;

import org.junit.Assert;
import org.junit.Test;

public class LongestPalindromicSubstringTest {
  @Test
  public void longestPalindromeTest1() {
    String s = "babad";
    String expected = "bab";
    Assert.assertEquals(expected, new LongestPalindromicSubstring().longestPalindrome(s));
  }

  @Test
  public void longestPalindromeTest2() {
    String s = "cbbd";
    String expected = "bb";
    Assert.assertEquals(expected, new LongestPalindromicSubstring().longestPalindrome(s));
  }

  @Test
  public void longestPalindromeTest3() {
    String s = "aaabaaaa";
    String expected = "aaabaaa";
    Assert.assertEquals(expected, new LongestPalindromicSubstring().longestPalindrome(s));
  }

  @Test
  public void longestPalindromeTest4() {
    String s = "";
    String expected = "";
    Assert.assertEquals(expected, new LongestPalindromicSubstring().longestPalindrome(s));
  }
}
